using System;
using System.IO;
using System.Threading.Tasks;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using Microsoft.Win32;
using ModernWpf.Controls;

namespace IntCoachAuswerter.Pages.LoadingPage
{
    public partial class LoadingPage
    {
        public LoadingPage()
        {
            InitializeComponent();
            InitializeImage();
        }

        private void InitializeImage()
        {
            var binaryArray = Convert.FromBase64String(
                "");
            var binaryImage = new BitmapImage();
            binaryImage.BeginInit();
            binaryImage.StreamSource = new MemoryStream(binaryArray);
            binaryImage.EndInit();

            image.Source = binaryImage;
        }

        private async void OpenNewPatientFileButtonClick(object sender, RoutedEventArgs e)
        {
            var openFileDialog = new OpenFileDialog
            {
                Filter = "Textdatei (*.txt)|*.txt|All files (*.*)|*.*",
                InitialDirectory = Environment.GetFolderPath(Environment.SpecialFolder.Desktop)
            };
            if (openFileDialog.ShowDialog() == true)
            {
                var newPatientFileString = File.ReadAllText(openFileDialog.FileName);
                var contentDialogResult = await ImportNewPatientFileContentDialog(newPatientFileString);
                if (contentDialogResult == ContentDialogResult.Primary)
                {
                    var patientDataList = PatientFileReaderService.Read("PatientData.json");
                    var overviewPage = new OverviewPage.OverviewPage(patientDataList);
                    _NavigationFrame.Navigate(overviewPage);
                }
            }
        }

        private async Task<ContentDialogResult> ImportNewPatientFileContentDialog(string newPatientFileString)
        {
            var importNewPatientFileContentDialog = new ContentDialog
            {
                Title = "Patientendaten importieren?",
                Content = new TextBox()
                {
                    TextAlignment = TextAlignment.Left,
                    Background = Brushes.White,
                    Foreground = Brushes.Black,
                    Margin = new Thickness(5),
                    Text = newPatientFileString
                },
                PrimaryButtonText = "Importieren",
                IsPrimaryButtonEnabled = false,
                SecondaryButtonText = "Validieren",
                CloseButtonText = "Abbrechen",
                Background = Brushes.DimGray
            };

            var importContentDialogResult = await importNewPatientFileContentDialog.ShowAsync();

            if (importContentDialogResult == ContentDialogResult.Secondary)
            {
                var patientFileValidator = new PatientFileValidateService();
                var result = patientFileValidator.Validate(newPatientFileString);
                var validateNewPatientFileContentDialog = new ContentDialog
                {
                    Title = "Patientendaten valid?",
                    Content = new TextBox()
                    {
                        TextAlignment = TextAlignment.Left,
                        Background = Brushes.White,
                        Foreground = Brushes.Black,
                        Margin = new Thickness(5),
                        Text = result
                            ? "Patientendaten fehlerfrei importierbar."
                            : "Patientendaten nicht importierbar. Bitte überprüfen Sie die Daten."
                    },
                    PrimaryButtonText = "Importieren",
                    IsPrimaryButtonEnabled = result,
                    CloseButtonText = "Abbrechen",
                    Background = Brushes.DimGray
                };
                await validateNewPatientFileContentDialog.ShowAsync();

                if (result)
                {
                    var patientFileImporter = new PatientFileImportService();
                    patientFileImporter.Import(newPatientFileString);
                    return ContentDialogResult.Primary;
                }

                return ContentDialogResult.None;
            }

            return ContentDialogResult.None;
        }
    }
}